import React, { Component } from 'react'
import NavbarContent from './NavbarContent';

export default class Navbar extends Component {
  constructor (props) {
    super(props)
    this.state = {
      positionFixed: false
    }
    this.yPos = 0
  }

  componentDidMount(){
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleScroll = () => {
    const newYPos = window.scrollY;
    console.log(newYPos)
    if (newYPos > 62 && newYPos < this.yPos){
      this.setState({
        positionFixed: true
      })
    }
    else if (newYPos<=1 || newYPos > this.yPos){
      this.setState({
        positionFixed: false
      })
    }
    this.yPos = newYPos
  }

  getPosition = () => {
    const {positionFixed} = this.state
    return positionFixed ? 'fixed' : ''
  }

  render() {
    const {positionFixed} = this.state
    return (
      <React.Fragment>
        {positionFixed &&
          <nav className='fixed'><NavbarContent /></nav>
        }
        <nav className='static'><NavbarContent /></nav>
        <style jsx>{`
          .fixed {
            position: fixed;
            top: 0;
            background-color: white;
            transition: background 2s ease-in-out;
            box-shadow: 5px 5px 5px grey;
          }
          .static {
            margin-bottom: 100px;
          }
        `}</style>
      </React.Fragment>
    )
  }
}
