import React from 'react'

export default function ImageGrid(props) {
    const imagePath = 'static/'
    const {images} = props

    const renderImages = () => {
        const { images } = props
        return images.map(image => <div className={'item'}><img src={imagePath + image.thumbnail} /></div>)
    }

    return (
        <div>
            <div className={'gridContainer'}>
                {images.map(image => <div className={'item'}><img src={imagePath + image.thumbnail} /></div>)}
            </div>
            <style jsx>{`
                .gridContainer{
                    display: flex;
                    justify-content: center;
                    flex-wrap: wrap;
                }

                .item{
                    width: 400px;
                }
            `}</style>
        </div>
    )
}
