import Link from 'next/link'
import Head from 'next/head'
import React, { Component } from 'react'

export default class NavbarContent extends Component {

    onClickDropdown = () => {
        console.log('click')
    }

  render() {
    return (
        <div className='navbarContent'>
            <nav className='nav'>
                <Head>
                    <link rel="icon" type="image/x-icon" href="/static/favicon/favicon.ico" />
                    <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.8.1/css/all.css" integrity="sha384-50oBUHEmvpQ+1lW4y57PTFmhCaXp0ML5d60M1M7uH2+nqUivzIebhndOJK28anvf" crossorigin="anonymous"></link>
                </Head>
                <div className='logo'><h1>Ifa Brand</h1></div>
                <div className='menuDropdown' onClick={this.onClickDropdown}><i className="fas fa-bars" /></div>
                <ul className='navItems'>
                    <li><Link href={`/work`}><a>Work</a></Link></li>
                    <li><Link href={`/about`}><a>About</a></Link></li>
                    <li><Link href={`/shop`}><a>Shop</a></Link></li>
                </ul>
            </nav>
            <style jsx>{`
              * {
                font-family: 'Roboto', sans-serif;
              }

              nav {
                display: flex;
            }

              ul {
                display: flex;
                padding: 0;
              }

              li {
                display:flex;
                list-style: none;
                align-items: flex-end;
                margin-right: 30px;
              }

              h1 {
                font-size: 30px;
                margin: 0;
                margin-right: 60px;
              }

              a {
                color:unset;
                cursor: pointer;
                text-decoration: none;
              }

              a:hover {
                opacity: 0.6;
              }

              .fixed {
                position: fixed;
                background-color: white;
              }

              .menuDropdown{
                  color: black;
                  font-size: 40px;
                  display: none;
                  position: fixed;
                  right: 30px;
                  top: 20px;
              }
              @media (max-width: 950px) {
                .nav {
                    display: flex;
                    justify-content: space-between;
                }
                .logo {
                    margin-left: 50px;
                }
              }
              @media (max-width: 500px) {
                .nav {
                    width: 100vw;
                    padding-top: 20px;
                    height: 50px;
                    position: fixed;
                    top: 0;
                    background-color: white;
                }
                .menuDropdown{
                    display: unset;
                }
                .navItems{
                    position: fixed;
                    top: 50px;
                    right: 0px;
                    display: flex;
                    padding-right: 50px;
                    flex-direction: column;
                    background-color: white;
                    box-shadow: 0px 0px 5px 0px;
                }
                li{
                    font-size: 30px;
                    margin: 10px;
                }
              }

            `}</style>
        </div>
      )
  }
}
