import React from 'react'

export default function ImageGrid(props) {
    const imagePath = 'static/'

    const renderImages = () => {
        const { images } = props
        return images.map(image => <div className={'gridItem'}><img src={imagePath + image.thumbnail} /></div>)
    }

    return (
        <div>
            <div className={'gridContainer'}>
                {renderImages()}
            </div>
            <style jsx>{`
                .gridContainer{
                    display: flex;
                    justify-content: center;
                    flex-wrap: wrap;
                }
                .gridItem{
                    display: flex;
                    margin: 10px;
                }
            `}</style>
        </div>
    )
}
