import Link from 'next/link'
import Head from 'next/head'
import React, { Component } from 'react'

export default class Navbar extends Component {
  constructor (props) {
    super(props)
    this.state = {
      positionFixed: false
    }
    this.yPos = 0
  }

  componentDidMount(){
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleScroll = () => {
    const newYPos = window.scrollY;
    if (!newYPos) return
    console.log(newYPos)
    if (newYPos > 62 && newYPos < this.yPos){
      this.setState({
        positionFixed: true
      })
    }
    if (newYpos > this.yPos){
      this.setState({
        positionFixed: false
      })
    }
    this.yPos = newYPos
  }

  getPosition = () => {
    const {positionFixed} = this.state
    return positionFixed ? 'fixed' : ''
  }

  render() {
    return (
      <nav className={this.getPosition()}>
        <Head>
          <link rel="icon" type="image/x-icon" href="/static/favicon/favicon.ico" />
        </Head>
        <ul>
          <li><h1>Ifa Brand</h1></li>
          <li><Link href={`/work`}><a>Work</a></Link></li>
          <li><Link href={`/about`}><a>About</a></Link></li>
          <li><Link href={`/shop`}><a>Shop</a></Link></li>
        </ul>
        <style jsx>{`
          * {
            font-family: 'Roboto', sans-serif;
          }

          nav {
            margin-bottom: 100px;
          }

          ul {
            display: flex;
            padding: 0;
          }

          li {
            display:flex;
            list-style: none;
            align-items: flex-end;
            margin-right: 30px;
          }

          h1 {
            font-size: 30px;
            margin: 0;
            margin-right: 60px;
          }

          a {
            color:unset;
            cursor: pointer;
            text-decoration: none;
          }

          a:hover {
            opacity: 0.6;
          }

          .fixed {
            position: fixed;
            background-color: white;
          }
        `}</style>
      </nav>
    )
  }
}
