import React from 'react'
import Head from 'next/head';

export default function ImageModal(props) {
    const imagePath = 'static/'
    const {image, onClickClose} = props
    return (
        <div className={'modal'}>
            <Head>
                <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.8.1/css/all.css" integrity="sha384-50oBUHEmvpQ+1lW4y57PTFmhCaXp0ML5d60M1M7uH2+nqUivzIebhndOJK28anvf" crossorigin="anonymous"></link>
            </Head>

            <div>
                <div className={'closeButton'} onClick={onClickClose}>
                    <i className="far fa-times-circle" />
                </div>
                <div className={'carousel'}>
                    <i className="arrow fas fa-chevron-left"></i>
                    <img src={imagePath + image.largeImage} />
                    <i className="arrow fas fa-chevron-right"></i>
                </div>
            </div>
            <style jsx>{`
                    * {
                    font-family: 'Roboto', sans-serif;
                    }
                    .modal{
                        margin:0;
                        padding:0;
                        position:fixed;
                        top:0;
                        left:0;
                        width: 100%;
                        height: 100%;
                        background-color: rgba(0, 0, 0, 0.6);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }
                    img{
                        max-width: 600px;
                    }
                    .closeButton{
                        position: relative;
                        left: 80px;
                        top: 0;
                        cursor: pointer;
                        font-size: 50px;
                        display: flex;
                        justify-content: flex-end;
                        color:white;
                    }
                    .carousel{
                        display:flex;
                    }
                    .arrow{
                        color: white;
                        font-size: 50px
                    }
            `}</style>
        </div>
    )
}
