import React from 'react'

export default function ImageGrid(props) {
    const imagePath = 'static/'
    const {images, onClickImage} = props

    return (
        <div>
            <div className={'gridContainer'}>
                {images.map((image, i) =>
                    <div
                        onClick={onClickImage(i)}
                        className={'gridItem'}>
                        <img src={imagePath + image.thumbnail} />
                    </div>)
                }
            </div>
            <style jsx>{`
                .gridContainer{
                    display: flex;
                    justify-content: center;
                    flex-wrap: wrap;
                    max-width: 1249px;
                    margin: 0px auto;
                }

                .gridItem{
                    cursor: pointer;
                    margin: 10px;
                }
            `}</style>
        </div>
    )
}
