import Navbar from '../components/Navbar';
import React, { Component } from 'react'
import ImageGrid from '../components/ImageGrid';
import ImageModal from '../components/ImageModal';

export default class work extends Component {

    constructor(props){
        super(props)
        this.state = {
            images: undefined,
            selectedIndex: undefined
        }
    }

    componentDidMount(){
        const images = require('../static/images.json').images;
        this.setState({images})
    }

    onClickImage = (index) => {
        this.setState({selectedIndex: index})
    }

    closeImageModal = () => this.setState({selectedIndex: undefined})

    onClickNextImage = () => {
        const {selectedIndex, images} = this.state
        if (selectedIndex < images.length-1){
            this.setState({selectedIndex: selectedIndex+1})
        }
    }

    onClickPrevImage = () => {
        const {selectedIndex} = this.state
        if (selectedIndex > 0){
            this.setState({selectedIndex: selectedIndex-1})
        }
    }

    render() {
        const {images, selectedIndex} = this.state
        return (
            <div>
                <head>
                    <link href="https://fonts.googleapis.com/css?family=Roboto" rel="stylesheet"></link>
                </head>
                {selectedIndex!==undefined &&
                    <ImageModal image={images[selectedIndex]} onClickClose={this.closeImageModal} onClickNext={this.onClickNextImage}/>
                }
                <Navbar />
                {images &&
                    <ImageGrid images={images} onClickImage={this.onClickImage} onClickPrev={this.onClickPrevImage}/>
                }
                <style jsx>{`
                    * {
                    font-family: 'Roboto', sans-serif;
                    }
                    div{
                        margin:0;
                        padding:10px 50px;
                    }
                `}</style>
            </div>
        )
    }
}
