import React, { Component } from 'react'
import NavbarContent from './NavbarContent';

export default class Navbar extends Component {
  constructor (props) {
    super(props)
    this.state = {
      positionFixed: false,
      smallScreen: false
    }
    this.yPos = 0
  }

  componentDidMount(){
    window.addEventListener('scroll', this.handleScroll);
    window.addEventListener('onresize', this.handleResize)
    this.handleResize()
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleResize = () => {
    console.log('handle resize')
    const {smallScreen} = this.state
    const width = window.innerWidth
    let newSmallScreen = false
    if (width > 500){
      newSmallScreen = true
    }
    if ( newSmallScreen !== smallScreen){
      this.setState({smallScreen: newSmallScreen})
    }
  }

  handleScroll = () => {
    const newYPos = window.scrollY;
    console.log(newYPos)
    if (newYPos > 62 && newYPos < this.yPos){
      this.setState({
        positionFixed: true
      })
    }
    else if (newYPos<=1 || newYPos > this.yPos){
      this.setState({
        positionFixed: false
      })
    }
    this.yPos = newYPos
  }

  getPosition = () => {
    const {positionFixed} = this.state
    return positionFixed ? 'fixed' : ''
  }

  render() {
    const {positionFixed, smallScreen} = this.state
    const {forceRemoveNav} = this.props
    console.log(smallScreen)
    return (
      <React.Fragment>
        {(smallScreen || (positionFixed && !forceRemoveNav)) &&
          <nav className='fixed'><NavbarContent /></nav>
        }
        <nav className='static'><NavbarContent /></nav>
        <style jsx>{`
          .fixed {
            position: fixed;
            top: 0;
            background-color: white;
            box-shadow: 5px 5px 5px;
            -webkit-animation-name: example; /* Safari 4.0 - 8.0 */
            -webkit-animation-duration: 0.5; /* Safari 4.0 - 8.0 */
            animation-name: example;
            animation-duration: 0.5s;
          }
          .static {
            margin-bottom: 100px;
          }
          /* Safari 4.0 - 8.0 */
          @-webkit-keyframes example {
            from {opacity: 0;}
            to {opacity: 1;}
          }

          /* Standard syntax */
          @keyframes example {
            from {opacity: 0;}
            to {opacity: 1;}
          }
        `}</style>
            <style jsx global>{`
        body {
            margin:0;
            padding:0;
        }
    `}</style>
      </React.Fragment>
    )
  }
}
