import React from 'react'

export default function ImageGrid(props) {
    const imagePath = 'static/'

    const renderImages = () => {
        const { images } = props
        return images.map(image => <div className={'grid-item'}><img src={imagePath + image.thumbnail} /></div>)
    }

    return (
        <div>
            <div className={'gridContainer'}>
                {renderImages()}
            </div>
            <style jsx>{`
                .gridContainer{
                    display: grid;
                    grid-gap: 50px;
                    grid-template-columns: auto auto auto auto;
                }
            `}</style>
        </div>
    )
}
