import React, { Component } from 'react'
import Head from 'next/head';

export default class ImageModal extends Component {

    static getDerivedStateFromProps(props, state) {
        const {selectedImageIndex} = props
        return {
            index: selectedImageIndex
        }
    }

    render() {
    const imagePath = 'static/'
    const {images, onClickClose} = this.props
    const {index} = this.state

    const image = images[index]


    return (
        <div className={'modal'}>
            <Head>
                <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.8.1/css/all.css" integrity="sha384-50oBUHEmvpQ+1lW4y57PTFmhCaXp0ML5d60M1M7uH2+nqUivzIebhndOJK28anvf" crossorigin="anonymous"></link>
            </Head>

            <div>
                <div className={'closeButton'} onClick={onClickClose}>
                    <i className="far fa-times-circle" />
                </div>
                <div className={'carousel'}>
                    <i onClick={this.prevImage} className="arrow fas fa-chevron-left"></i>
                    <img src={imagePath + image.largeImage} />
                    <i onClick={this.nextImage} className="arrow fas fa-chevron-right"></i>
                </div>
            </div>
            <style jsx>{`
                    * {
                    font-family: 'Roboto', sans-serif;
                    }
                    .modal{
                        margin:0;
                        padding:0;
                        position:fixed;
                        top:0;
                        left:0;
                        width: 100%;
                        height: 100%;
                        background-color: rgba(0, 0, 0, 0.6);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }
                    img{
                        max-width: 800px;
                    }
                    .closeButton{
                        position: relative;
                        left: 0;
                        top: 0;
                        cursor: pointer;
                        font-size: 50px;
                        display: flex;
                        justify-content: flex-end;
                        color:white;
                    }
                    .carousel{
                        display:flex;
                        justify-content: center;
                        align-items: center;
                    }
                    .arrow{
                        color: white;
                        font-size: 50px;
                        margin: 50px;
                        cursor: pointer;
                    }
            `}</style>
        </div>
    )
}
}
